function new_total(a){var b=$(a).parent().parent(),c=Number($(a).val()),d=Number(b.children("td.oldCount").text()),e=$("#stock_adjustment_reason").val();switch(e){case"Defective":case"Donation":var f=d-c;break;default:var f=d+c}isNaN(f)||(b.children("td.newTotal").empty(),b.children("td.newTotal").text(f))}function start_spinner(a){$(a).html('Please wait...<img src="/assets/ajax-loader.gif" alt="Data Loading" />')}function stop_spinner(a){$(a).html("")}function update_all_totals(){$(".newCount input").trigger("change")}$(document).ready(function(){$("#method_select").change(function(){$.post("/ajax/methods",{method:$("#method").val()},function(a){stop_spinner($("#selector")),$("#selector").html(a)}),start_spinner($("#selector"))}),$("#receiving_items").bind("ajax:success",function(a,b,c,d){$("#stepThree").html(d.responseText)}),$("#stock_adjustment_items").bind("ajax:success",function(a,b,c,d){$("#stepThree").html(d.responseText)})})